import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import Title from './components/Title';
import Product from './components/Product';
import Cart from './components/Cart';

class App extends Component {

  state = {
      products: [
          {id:1, title: "Trek Bike Fuel EX 30", price: 129},
          {id:2, title: "Bianchi Bike ZR 217", price: 299},
          {id:3, title: "Spacialized Bike TW 7", price: 99},
          {id:4, title: "Nixon Bike R152565", price: 179}
      ]
  ,
    historyCart:[],
    cart:[]
  }

  addProductToCart = (i,title,price) => {
      this.setState({cart:[{title:title, price:price, index:i, inCart:true},...this.state.cart]});
      alert("Product has been add to your cart");
  }

  showCart = () =>{
    return this.state.cart.map((product, i) => {
          return(
              <tr key={i}>
                  <td>{product.id}</td>
                  <td>{product.title}</td>
                  <td>{product.price}</td>
              </tr>
          )
      }); 
  } 

  render(){
    return (
      <div className="App">
        <Header />
        <Title title="Our Products" />
        <div className="products-wrapper">
          {this.state.products.map((item, i) => {
            return <Product title={item.title} price={item.price} index={i} add={this.addProductToCart} />
          })}
        </div>
        <div className="cart">
          <table>
            <tbody>
              {this.showCart()}
            </tbody>
          </table>
        </div>    
        </div>
    );
  }
}

export default App;
