import React, { Component } from 'react'
import Title from './Title';
export default class Cart extends Component {
    constructor(props){
        super(props)

        this.state = {
            products: this.props.products

        }
    }
    showTheCart = () =>{
        this.props.showCart(this.state.products);
    }

    render() {
        return (
            <div>
                <Title title="Cart" />
                <div className="cart">
                    <table>
                        <tbody>
                        </tbody>
                    </table>          
                </div>
                <span>Total : </span> <br/>
                <button style={{marginTop:'30px'}} className="add-to-cart">checkout</button>
            </div>
        )
    }
}
