export default class Header extends Component {
    constructor(props){
        super(props)
        
        this.state = {
            showProducts: false,
            showCart: true
        }
    }

    cartPage = () =>{
        this.props.showCart(this.state.showCart);
    }
    productsPage = () => {
        this.props.showProducts(this.state.showProducts);
    }

